mapper.update:
update product
p_name=#{p_name}, p_category=#{p_category}, p_s_category=#{p_s_category}, thumb=#{thumb}, p_price=#{p_price}, startdate=to_date(#{startdate},'YYYY-MM-DD'), enddate=to_date(#{enddate},'YYYY-MM-DD')
where p_num=#{p_num}

update P_DETAIL
pd_p_desc=#{pd_p_desc}, origin=#{origin}, local=#{local}, butchery=#{butchery}, weight=#{weight}, serialNum=#{serialNum}, retain=#{retain}, stock=#{stock}, pd_duedate=#{pd_duedate}
where pd_p_num=#{pd_p_num}
