mapper.select:

select * from member a left outer join memstatus b
			on a.m_status = b.msta_m_status
			where a.m_id=#{m_id}

mapper.update:
UPDATE shoppingcart_${shop_m_id}
SET quantity = ${quantity}
WHERE shop_p_num = ${shop_p_num}

update member
passwd=#{passwd}, m_name=#{m_name},
m_addr1=#{m_addr1}, m_addr2=#{m_addr2}, email=#{email}, telep=#{telep}, phone=#{phone}
where m_id=#{m_id}

UPDATE member
SET m_status = 1050
WHERE m_id = #{m_id}
